import React from 'react';
import {
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, LabelList
} from 'recharts';

import '../components/FailureChart.css';

const FailureChart = ({ reports }) => {
  const data = [];
  let totaltests = 0;

  reports.forEach(report => {
    if (report.result === 'Fail') {
      const user = data.find(item => item.name === report.user);
      if (user) {
        user.failures += 1;
      } else {
        data.push({ name: report.user, failures: 1 });
      }
    }
  });

  return (
    <div className="failure-chart-container card">
      <div className="card-content">
        <h2 className="failure-chart-title">Users Tests Failure Analysis</h2>
        <ResponsiveContainer width="100%" height={400}>
          <BarChart data={data} margin={{ top: 20, right: 20, left: 20, bottom: 20 }}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="name" />
            <YAxis />
            <Tooltip />
            <Bar dataKey="failures" fill="#82ca9d" barSize={50}>
              <LabelList dataKey="failures" position="top" formatter={(value) => `${value}Failures`} />
            </Bar>
          </BarChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
};

export default FailureChart;
