import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ReportsTable from './ReportsTable';
import ReportsChart from './ReportsChart';
import { Container } from '@mui/material';
import FailureChart from '../components/FailureChart';

const ReportsPage = () => {
  const [reports, setReports] = useState([]);
  const [passCount, setPassCount] = useState(0);
  const [failCount, setFailCount] = useState(0);
  const [unresolvedCount, setUnresolvedCount] = useState(0);
  const [filteredReports, setFilteredReports] = useState(null); // State to manage filtered reports

  useEffect(() => {
    axios.get('http://localhost:3000/api/reports')
      .then(response => {
        setReports(response.data);

        const pass = response.data.filter(report => report.result === 'Pass').length;
        const fail = response.data.filter(report => report.result === 'Fail').length;
        const unresolved = response.data.filter(report => report.result === 'Unresolved').length;

        setPassCount(pass);
        setFailCount(fail);
        setUnresolvedCount(unresolved);
      })
      .catch(error => console.error('Error fetching the reports:', error));
  }, []);

  const handleFilter = (status) => {
    if (status) {
      const filtered = reports.filter(report => report.result === status);
      setFilteredReports(filtered);
    } else {
      setFilteredReports(null); // Reset to show all reports
    }
  };

  return (
    <Container maxWidth="lg">
      <ReportsChart 
        passCount={passCount} 
        failCount={failCount} 
        unresolvedCount={unresolvedCount} 
        totalTests={reports.length} 
        onSegmentClick={handleFilter} // Pass the filter handler to the chart
        reports={reports} 
      />
      <ReportsTable reports={filteredReports || reports} /> {/* Show filtered or all reports */}
    </Container>
  );
};

export default ReportsPage;
