import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ReportsTable from './ReportsTable';
import ReportsChart from './ReportsChart';
import { Container } from '@mui/material';

const ReportsPage = () => {
  const [reports, setReports] = useState([]);
  const [passCount, setPassCount] = useState(0);
  const [failCount, setFailCount] = useState(0);
  const [unresolvedCount, setUnresolvedCount] = useState(0);

  useEffect(() => {
    axios.get('http://localhost:3000/api/reports')
      .then(response => {
        setReports(response.data);

        const pass = response.data.filter(report => report.result === 'Pass').length;
        const fail = response.data.filter(report => report.result === 'Fail').length;
        const unresolved = response.data.filter(report => report.result === 'Unresolved').length;

        setPassCount(pass);
        setFailCount(fail);
        setUnresolvedCount(unresolved);
      })
      .catch(error => console.error('Error fetching the reports:', error));
  }, []);

  return (
    <Container maxWidth="lg">
      <ReportsTable reports={reports} />
      <ReportsChart 
        passCount={passCount} 
        failCount={failCount} 
        unresolvedCount={unresolvedCount} 
        totalTests={reports.length} 
      />
    </Container>
  );

};

export default ReportsPage;
