import React from 'react';
import { Table, TableBody, TableCell, TableHead, TableRow } from '@mui/material';
import OpenJiraButton from '../components/OpenJiraButton'; // ייבוא של OpenJiraButton
import '../components/ReportsTable.css';

const ReportsTable = ({ reports }) => {
  // Function to determine the color based on the result
  const getResultColor = (result) => {
    switch (result) {
      case 'Pass':
        return 'green';
      case 'Fail':
        return 'red';
      case 'Unresolved':
        return 'yellow';
      default:
        return 'black'; // Default color
    }
  };

  return (
    <Table>
      <TableHead>
        <TableRow>
          <TableCell>ID</TableCell>
          <TableCell>Test</TableCell>
          <TableCell>Version Build</TableCell>
          <TableCell>Cluster</TableCell>
          <TableCell>Result</TableCell>
          <TableCell>Failure Reason</TableCell>
          <TableCell>Runtime</TableCell>
          <TableCell>Date</TableCell>
          <TableCell>User</TableCell>
          <TableCell>Actions</TableCell> {/* עמודה חדשה עבור הכפתור */}
        </TableRow>
      </TableHead>
      <TableBody>
        {reports.map((report) => (
          <TableRow key={report.id}>
            <TableCell>{report.id}</TableCell>
            <TableCell>{report.test}</TableCell>
            <TableCell>{report.versionBuild}</TableCell>
            <TableCell>{report.cluster}</TableCell>
            <TableCell style={{ color: getResultColor(report.result) }}>
              {report.result}
            </TableCell>
            <TableCell style={{ color: getResultColor(report.result) }}>
              {report.failureReason}
            </TableCell>
            <TableCell>{report.runtime}</TableCell>
            <TableCell>{report.date}</TableCell>
            <TableCell>{report.user}</TableCell>
            <TableCell>
              {report.result !== 'Pass' && (
                <OpenJiraButton 
                  issueDetails={{
                    summary: `Failure in ${report.test}`,  // התאמה אישית של ה-Summary
                    description: `Test ID: ${report.id}
                    Test Name: ${report.test}
                    Version Build: ${report.versionBuild}
                    Cluster: ${report.cluster}
                    Result: ${report.result}
                    Failure Reason: ${report.failureReason}
                    Runtime: ${report.runtime}
                    Date: ${report.date}
                    User: ${report.user}`
                  }}
                />
              )}
            </TableCell> {/* תא חדש עבור הכפתור */}
          </TableRow>
        ))}
      </TableBody>
    </Table>
  );
};

export default ReportsTable;
