import React from 'react';
import { Bar } from 'react-chartjs-2';
import 'chart.js/auto';

const FailureChart = ({ reports }) => {
  // Process data to count failed tests per user
  const userFailures = {};

  reports.forEach(report => {
    if (report.result === 'Fail') {
      if (!userFailures[report.user]) {
        userFailures[report.user] = 0;
      }
      userFailures[report.user]++;
    }
  });

  const labels = Object.keys(userFailures);
  const values = Object.values(userFailures);

  const data = {
    labels: labels, // User names on x-axis
    datasets: [{
      label: 'Failed Tests',
      data: values, // Number of failed tests on y-axis
      backgroundColor: 'rgba(255, 99, 132, 0.2)',
      borderColor: 'rgba(255, 99, 132, 1)',
      borderWidth: 1
    }]
  };

  const options = {
    scales: {
      y: {
        beginAtZero: true
      }
    }
  };

  return (
    <div>
      <h2>Test Failure Analysis</h2>
      <Bar data={data} options={options} />
    </div>
  );
};

export default FailureChart;
